  const { data, error } = await Noodl.Variables.supabase
    .from('resumes')
    .select(`
      *,
      applicants(*),
      education(*),
      work_experience(*),
      skills(*)
    `)
    .eq('id', id)
    .order('sort_order', {foreignTable: 'education'})
    .order('sort_order', {foreignTable: 'work_experience'})
    .order('sort_order', {foreignTable: 'skills'})
    .single();

  if (error) throw error;

  Outputs.Object = Noodl.Object.create({...data}).getId();

  Outputs.Success();
} catch (error) {
  Outputs.error = error;
  Outputs.Failure();
}

const supabase = Noodl.Variables.supabase || null;
