// Decrease loading spinner running counter
Noodl.Variables.NumLoadingSpinnerRunning--

// When we reach zero, hide the loading spinner
if(Noodl.Variables.NumLoadingSpinnerRunning < 1)
    Noodl.Events.emit("Hide loader");

// If we have multiple show signals sent, only update the
// running count
if(Noodl.Variables.NumLoadingSpinnerRunning > 0)
    Noodl.Variables.NumLoadingSpinnerRunning++
else {
    // If this is the first show signal, show the popup
    Noodl.Variables.NumLoadingSpinnerRunning = 1
    Outputs.ShowPopup()
}

Noodl.Events.on("Hide loader", function () {
    Outputs.hide();
});

// Set the initial width to 0
let width = 0;

// Set the growth rate to 1/60th of the total width
const growthRate = 100 / 60;

// Create an interval that runs every second
const interval = setInterval(() => {
  // Increase the width by the growth rate every second
  width += growthRate;

  // Output the current width
  Outputs.Width = width;

  // If the width is 50 or more, double the growth rate
  if (width >= 50) {
    growthRate *= 2;
  }

  // If the width is 75 or more, halve the growth rate
  if (width >= 75) {
    growthRate /= 2;
  }

  // If the width is 100 or more, clear the interval and send the Success signal
  if (width >= 100) {
    clearInterval(interval);
    Outputs.Success();
  }
}, 1000);

