// Get the text from the input node
const textToCopy = Inputs.TextNode.props.text;
Outputs.Text = textToCopy;

// If the text is empty, return and do not proceed
if (!textToCopy) return;

try {
  // Attempt to write the text to the clipboard
  navigator.clipboard.writeText(textToCopy);

  // If successful, send the Success signal
  Outputs.Success();
} catch (error) {
  // If an error occurs, set the error output and send the Failure signal
  Outputs.error = error;
  Outputs.Failure();
}

try {
  // Get the template from the localStorage
  const template = localStorage.getItem('template');

  // If the template is not found in the localStorage, throw an error
  if (!template) throw new Error('Template not found');

  // Output the template
  Outputs.Template = template;
  Outputs.Success();
} catch (error) {
  // Output the error and send the Failure signal
  Outputs.Error = error;
  Outputs.Failure();
}

const template = Inputs.Template;

switch(template) {
    case "template-one":
        Outputs.CreateTemplateOne()
        break;
    case "template-two":
        Outputs.CreateTemplateTwo()
        break;
    case "template-three":
        Outputs.CreateTemplateThree()
        break;
}

const resumeId = Inputs.ResumeId || '';
if (!resumeId) throw new Error('Invalid Resume ID');

let imagePath = Inputs.ImagePath || '';
if (!imagePath) throw new Error('Invalid Image Path');
if (imagePath === 'null') {
  imagePath = null;
}

Outputs.Path = imagePath;

try {
  const { data, error } = await Noodl.Variables.supabase
    .from('resumes')
    .update({ image_path: imagePath })
    .eq( 'id', resumeId );

  if (error) throw error;

  Outputs.Success();
} catch (error) {
  Outputs.error = error.message;
  Outputs.Failure();
}

