Node.Setters.mediaQuery = function(value) {

    if(this.query) {
        this.query.removeListener(this.listener);
    }

    this.listener = query => {
      if (query.matches) {
        Node.setOutputs({match: true, noMatch: false});
      } else {
        Node.setOutputs({match: false, noMatch: true});
      }
    }

    this.query = window.matchMedia(value);
    this.listener(this.query);
    this.query.addListener(this.listener);

}

Node.OnDestroy = function() {
    if(this.query) {
        this.query.removeListener(this.listener);
        this.query = undefined;
        this.listener = undefined;
    }
}

