var inited = false;
var reader;
var img;
var imageWidth, imageHeight;
var canvas;
var maskWidth, maskHeight;


Script.Inputs = {
    ImageFile:'*'
}

Script.Outputs = {
    CropFile:'cloudfile'
}

function calculateScale ( arenaDims, imageDims) {
    let wr = arenaDims.w / imageDims.w;
    let hr = arenaDims.h / imageDims.h;
    if (wr < hr) {
        return wr;
    }
    else {
        return hr;
    }
}

Script.Setters.ArenaWidth = function () {
    refreshDimensions ();
}

Script.Setters.ArenaHeight = function () {
    refreshDimensions ();
}

function refreshDimensions () {
    if (inited === false || Script.Inputs.ArenaWidth === undefined || Script.Inputs.ArenaHeight === undefined) {
        return false;
    }
    else {

        // figure out scale factor
        scaleFactor = calculateScale (
            {w:Script.Inputs.ArenaWidth, h:Script.Inputs.ArenaHeight},
            {w:imageWidth, h:imageHeight});
        
        Script.Outputs.InitialZoomLevel = scaleFactor;
    
        // calculate the relative size of the mask contra image
        let wr = Script.Inputs.MaskRatioWidth / imageWidth;
        let hr = Script.Inputs.MaskRatioHeight / imageHeight;
    
        if (wr === hr) {
            maskWidth = imageWidth;
            maskHeight = imageHeight;
        }
        else if (wr > hr) {
            // width is most similar, align width
            let hx = Script.Inputs.MaskRatioHeight * imageWidth / Script.Inputs.MaskRatioWidth;
            maskWidth = imageWidth;
            maskHeight = hx;
        }
        else {
            let wx = Script.Inputs.MaskRatioWidth * imageHeight / Script.Inputs.MaskRatioHeight;
            maskWidth = wx;
            maskHeight = imageHeight;
        }
        Script.Outputs.MaskWidth = maskWidth * scaleFactor;
        Script.Outputs.MaskHeight = maskHeight * scaleFactor;
        Script.Outputs.InitialZoomLevel = scaleFactor;
        return true;
    }
}

Script.Signals.Init = function () {
    inited = false;
    reader = new FileReader();
    reader.readAsDataURL(Node.Inputs.ImageFile);
    Node.Outputs.ImageUrl = URL.createObjectURL(Node.Inputs.ImageFile);

    reader.onload = function (e) {
        img = document.createElement("img");
        img.src = e.target.result;
        img.onload = function() {
            imageWidth = img.width;
            imageHeight = img.height;
            inited = true;
            refreshDimensions ();
        }
    };
}

Script.Signals.Crop = function () {
    canvas = document.createElement("canvas");
    // figure out the longest dimension and scale if necessary
    let targetImageScaleFactor = 1;
    
    if (maskWidth > maskHeight) {
        if (maskWidth > Script.Inputs.MaxResolutions) {
            targetImageScaleFactor = Script.Inputs.MaxResolutions / maskWidth;
        }
    }
    else {
        if (maskHeight > Script.Inputs.MaxResolutions) {
            targetImageScaleFactor = Script.Inputs.MaxResolutions / maskHeight;
        }
    }
    
    
    // scale up image space coords of the mask
    let sx = Script.Inputs.CropXPos === undefined?0:Math.floor (Script.Inputs.CropXPos);
    let sy = Script.Inputs.CropYPos === undefined?0:Math.floor (Script.Inputs.CropYPos);
    let sWidth = Math.floor (Script.Inputs.CropWidth);
    let sHeight = Math.floor (Script.Inputs.CropHeight);
    let dWidth = Math.floor (targetImageScaleFactor*maskWidth);
    let dHeight = Math.floor (targetImageScaleFactor*maskHeight);
    canvas.width = dWidth;
    canvas.height = dHeight;
    
    let ctx = canvas.getContext("2d");
    ctx.fillStyle='white';
    ctx.fillRect(0,0,dWidth,dHeight)
    ctx.drawImage(img, sx, sy, sWidth, sHeight, 0, 0, dWidth, dHeight);
    let filename =  Script.Inputs.FileName === undefined?"img"+Math.round (Math.random()*1000000):Script.Inputs.FileName;
    let dataurl = canvas.toBlob(blob => {
        const file = new File([blob], filename);
        Script.Outputs.CropFile = file;
        Script.Outputs.CropFileSize = Math.round(file.size);
        
        Script.Outputs.CropFileUrl = URL.createObjectURL(file);
        
        Script.Outputs.CropDone();
    },'image/jpeg');   
}

var nativeImageHeight;
var nativeImageWidth;
var zoomLevel = 1;
var inited = false;
var imageXPos;
var imageYPos;
var delayedZoom = -1;

Script.Inputs = {
    ImageNode:'*',
    DragNode:'*'
}

// pinch zoom code

function addPinchZoomListeners () {
    if (Script.Inputs.DragNode !== undefined) {
        Script.Inputs.DragNode.getDOMElement().addEventListener ('pointerdown', pzPointerDown);
        Script.Inputs.DragNode.getDOMElement().addEventListener ('pointermove', pzPointerMove);
        Script.Inputs.DragNode.getDOMElement().addEventListener ('pointerup', pzPointerUp);
    }
}

function removePinchZoomListeners () {
    if (Script.Inputs.DragNode !== undefined) {
        Script.Inputs.DragNode.getDOMElement().removeEventListener ('pointerdown', pzPointerDown);
        Script.Inputs.DragNode.getDOMElement().removeEventListener ('pointermove', pzPointerMove);
        Script.Inputs.DragNode.getDOMElement().removeEventListener ('pointerup', pzPointerUp);
    }
}

var pzEventCache = [];
var pzPrevDiff = -1;

function pzPointerDown (ev) {
    pzEventCache.push (ev);
}

function pzPointerUp (ev) {
    for (let i = 0; i < pzEventCache.length; i++) {
        let e = pzEventCache[i];
        if (e.pointerId === ev.pointerId) {
            pzEventCache.splice(i, 1);
            break;
        }
    }

    if (pzEventCache.length < 2) {
        pzPrevDiff = -1;
    }
}

function pzPointerMove (ev) {

    for (let i = 0; i < pzEventCache.length;i++) {
        let e = pzEventCache[i];
        if (e.pointerId === ev.pointerId) {
            pzEventCache[i] = ev;
            break;
        }
    }

    if (pzEventCache.length === 2) {
        let xDiff = Math.abs (pzEventCache[0].clientX-pzEventCache[1].clientX);
        if (pzPrevDiff > 0) {
            if (xDiff > pzPrevDiff) {
                zoomInSmall();
            }
            else if (xDiff < pzPrevDiff) {
                zoomOutSmall();
            }
        }
        pzPrevDiff = xDiff;
    }
}

Script.Signals.CancelMultiTouch = function () {
    if (Script.Signals.EnablePinchZooming) {
        pzEventCache = [];
    }
}

Script.Signals.Init = function () {
    if (Script.Inputs.ImageNode !== undefined) {
        nativeImageWidth = Script.Inputs.ImageNode.getDOMElement().naturalWidth;
        nativeImageHeight = Script.Inputs.ImageNode.getDOMElement().naturalHeight;

        if (Script.Inputs.EnablePinchZooming) {
            addPinchZoomListeners ();     
        }
    }
    
    imageXPos = 0;
    imageYPos = 0;
    inited = true;
    if (delayedZoom !== -1) {
        tryZoom (delayedZoom);
        delayedZoom = -1;
    }
    else {
        refreshView();
    }
    
}

function zoomInSmall () {
    tryZoom (zoomLevel * 1.01);
}

function zoomOutSmall () {
    tryZoom (zoomLevel * 0.99);
}

function zoomIn () {
    tryZoom (zoomLevel * 1.05);
}

function zoomOut () {
    tryZoom (zoomLevel * 0.95);
}

Script.Signals.ZoomIn = function () {
    zoomIn();
}

Script.Signals.ZoomOut = function () {
    zoomOut();
}

function tryZoom (value) {
    if (!inited) {
        return;
    }
    let oldZoomLevel = zoomLevel;
    if (value < Script.Inputs.MinZoomLevel) {
        // cant zoom out more
        zoomLevel = Script.Inputs.MinZoomLevel;
    }
    if (value > Script.Inputs.MaxZoomLevel) {
        // cant zoom out more
        zoomLevel = Script.Inputs.MaxZoomLevel ;
    }
    if (Script.Inputs.ConstraintToParent) {
        zoomLevel = value;
        if (zoomLevel * nativeImageWidth < Script.Inputs.ContainerWidth) {
            zoomLevel = Script.Inputs.ContainerWidth / nativeImageWidth;
        }
        if (zoomLevel * nativeImageHeight < Script.Inputs.ContainerHeight) {
            zoomLevel = Script.Inputs.ContainerHeight / nativeImageHeight;
        }
        // check if any of the edges are outside the container

        let lp = imageXPos * zoomLevel - nativeImageWidth * zoomLevel/2 + Script.Inputs.ContainerWidth/2;
        if (lp > 0) {
            imageXPos -= lp / zoomLevel;
        }

        let tp = imageYPos * zoomLevel - nativeImageHeight * zoomLevel/2 + Script.Inputs.ContainerHeight/2;
        if (tp > 0) {
            imageYPos -= tp / zoomLevel;
        }

        let rp = imageXPos * zoomLevel + nativeImageWidth * zoomLevel/2 - Script.Inputs.ContainerWidth/2;
        if (rp < 0) {
            imageXPos -= rp / zoomLevel;
        }
        let bp = imageYPos * zoomLevel + nativeImageHeight * zoomLevel/2 - Script.Inputs.ContainerHeight/2;
        if (bp < 0) {
            imageYPos -= bp / zoomLevel;
        }
    }
    else {
        zoomLevel = value;
    }

    if (zoomLevel !== oldZoomLevel) {
        refreshView();
    }
}

Script.Setters.SuggestedZoomLevel = function (value) {
    if (!inited) {
        delayedZoom = value;
    }
    else {
        tryZoom (value);
    }
    
}


Script.Setters.DeltaX = function (value) {
    if (!inited) {
        return;
    }
    let oldPos = imageXPos;
    imageXPos += value/zoomLevel;
    if (Script.Inputs.ConstraintToParent) {
        let leftLimit = -(nativeImageWidth - Script.Inputs.ContainerWidth/zoomLevel)/2;
        let rightLimit = (nativeImageWidth - Script.Inputs.ContainerWidth/zoomLevel)/2;
        
        if (imageXPos < leftLimit) {
            imageXPos = leftLimit;
        }
        else if (imageXPos > rightLimit) {
            imageXPos = rightLimit;
        }
    }
    if (oldPos !== imageXPos) {
        refreshView();
    }
    
}

Script.Setters.DeltaY = function (value) {
    if (!inited) {
        return;
    }
    let oldPos = imageYPos;
    imageYPos += value/zoomLevel;
    if (Script.Inputs.ConstraintToParent) {
        //let scaledImageHeight = nativeImageHeight * zoomLevel;
        let topLimit = -(nativeImageHeight - Script.Inputs.ContainerHeight/zoomLevel)/2;
        let bottomLimit = (nativeImageHeight - Script.Inputs.ContainerHeight/zoomLevel)/2; 
        
        if (imageYPos < topLimit) {
            imageYPos = topLimit;
        }
        else if (imageYPos > bottomLimit) {
            imageYPos = bottomLimit;
        }
    }
    if (oldPos !== imageYPos) {
        refreshView();
    }
    
}

Script.Setters.ContainerWidth = function (value) {
    refreshView();
}

Script.Setters.ContainerHeight = function (value) {
    refreshView();
}

function scrollWheelListener (event) {
    let delta = event.deltaY;
    if (delta < 0) {
        zoomIn();
    }
    else {
        zoomOut();
    }
}

Script.Setters.EnableScrollWheelZooming = function (value) {
    if (value === true) {
        window.addEventListener ('wheel', scrollWheelListener);
    }
    else {
        window.removeEventListener ('wheel', scrollWheelListener);
    }
}



Script.Setters.EnablePinchZooming = function (value) {
    if (value === true) {
        if (inited === true) {
            addPinchZoomListeners ();    
        }
    }
    if (value === false) {
        if (inited === true) {
            removePinchZoomListeners ();
        }
    }
}

function refreshView () {
    if (nativeImageHeight !== undefined &&
    nativeImageWidth !== undefined &&
    Script.Inputs.ContainerWidth !== undefined && Script.Inputs.ContainerHeight !== undefined) {        
       
        let scaledImageWidth = nativeImageWidth * zoomLevel;
        let scaledImageHeight = nativeImageHeight * zoomLevel;
        
        Script.Outputs.ImageXpos = imageXPos * zoomLevel + Script.Inputs.ContainerWidth/2 - scaledImageWidth/2;
        Script.Outputs.ImageYpos = imageYPos * zoomLevel + Script.Inputs.ContainerHeight/2 - scaledImageHeight/2;
        Script.Outputs.ImageWidth = zoomLevel * nativeImageWidth;

        // calculate visible area
        let visibleX = (nativeImageWidth - Script.Inputs.ContainerWidth/zoomLevel)/2 - imageXPos;///zoomLevel + nativeImageWidth/2;
        let visibleY = (nativeImageHeight - Script.Inputs.ContainerHeight/zoomLevel)/2 - imageYPos;
        Script.Outputs.VisibleXPos = visibleX;
        Script.Outputs.VisibleYPos = visibleY;
        let visibleWidth = nativeImageWidth * ((Script.Inputs.ContainerWidth/zoomLevel) / nativeImageWidth);
        let visibleHeight = nativeImageHeight * ((Script.Inputs.ContainerHeight/zoomLevel) / nativeImageHeight);
        Script.Outputs.VisibleWidth = visibleWidth;
        Script.Outputs.VisibleHeight = visibleHeight;
        Script.Outputs.ViewUpdated();
        
    }
}

Script.OnDestroy = function () {
    if (Script.Inputs.EnableScrollWheelZooming === true) {
        window.removeEventListener ('wheel', scrollWheelListener);   
    }
    /*if (Script.Inputs.EnablePinchZooming === true) {
        removePinchZoomListeners ();
    }*/
}

