const generateFilename = (file) => {
  const randomSequence = Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);
  const splitFileName = file.name.split('.');
  const newName = `${splitFileName.slice(0, -1).join('.')}_${randomSequence}.${splitFileName.pop()}`;
  return newName;
}

const uploadFile = async (supabase, userId, file) => {
  const newFileName = generateFilename(file);
  const { data: uploadData, error: uploadError } = await supabase.storage
    .from('resume_files')
    .upload(`${userId}/${newFileName}`, file);
  if (uploadError) throw new Error(uploadError.message);
  return uploadData.path;
}

const insertDataToTable = async (supabase, template, filePath, userId) => {
  const { data: insertData, error: insertError } = await supabase
    .from('resumes')
    .insert([{
      template: template,
      file_path: filePath,
      user_id: userId,
      processing: true,
      comparison_description: jobDecription,
      include_watermark: includeWatermark,
      watermark_path: watermarkPath,
      image_path: logoPath,
      anonymised: Inputs.Anonymised
    }])
    .select();
  if (insertError) throw new Error(insertError.message);
  return insertData;
}

