// Create a new file input element
const fileInput = document.createElement('input');

// Set the type to file to open the file picker
fileInput.type = 'file';

// Allow for multiple file selection
fileInput.multiple = true;

// Listen for the change event which is fired when files are selected
fileInput.addEventListener('change', (event) => {
  // Get the selected files from the event
  const files = event.target.files;

  // If no files were selected, send the Failure signal
  if (files.length === 0) {
    Outputs.Failure();
    return;
  }

  // Convert the FileList to an array
  const fileList = Array.from(files);

  // Map the array to an array of objects with file as a property
  const fileObjects = fileList.map(file => ({file}));

  // Send the file objects to the output
  Outputs.Files = fileObjects;

  // Send the Success signal
  Outputs.Success();
});

// Trigger the file picker
fileInput.click();

