// uniqueId is making sure that we only load a script once
// TODO: This could depend on the scriptUrl
const uniqueId = '--tinymce-js--'

// The script url that we want to import
const apiKey = Inputs.ApiKey || 'no-api-key';
const scriptUrl = `https://cdn.tiny.cloud/1/${apiKey}/tinymce/6/tinymce.min.js`;

// ---
// Advanced Code
const windowKey = '__' + uniqueId + '_onload';
function injectScript(onload) {
    if (document.querySelectorAll('#' + uniqueId).length === 0) {
        window[windowKey] = [onload]

        var script = document.createElement('script');
        script.id = uniqueId;
        script.setAttribute('src', scriptUrl);
        script.onload = () => {
            window[windowKey].forEach(cb => cb())
            window[windowKey] = []
        }
        document.head.appendChild(script)
    } else {
        if (window[windowKey].length > 0) {
            window[windowKey].push(onload)
        } else {
            onload()
        }
    }
}

injectScript(() => {
    Outputs.Loaded();
});

