parameters.code:
Script.Inputs = {
    element: {
        type: '*',
        displayName: 'Element',
        group: 'Setup',
    },
    acceptedFileTypes: {
        type: 'string',
        displayName: 'Accepted file types',
        group: 'Data'
    }
}

Script.Outputs = {
    File: '*',
    Path: 'string',
    Name: 'string',
    SizeInBytes: 'string',
    Type: 'string',
    Success: 'signal',
    // When the mouse is dragging a file over the dropzone
    OnDragOver: 'signal',
    OnDragLeave: 'signal',
}

// Cache all the acceptedFileTypes in an array as regex expressions
let acceptedFileTypesPatterns = []

Script.Setters.acceptedFileTypes = function (value) {
    // Clear the array of patterns,
    // since we will update them now.
    acceptedFileTypesPatterns = [];

    // If the value is empty we exit early,
    // and we already emptied the patterns array.
    if (!value) {
        return;
    }

    // We are following the same pattern as the "Open File Picker" node.
    // https://docs.noodl.net/2.6/nodes/utilities/open-file-picker/
    const patterns = value.split(",");
    if (patterns.length > 0) {
        patterns.forEach((pattern) => {
            const regExp = ".*" + pattern.replace('*', '.*') + ".*";
            acceptedFileTypesPatterns.push(new RegExp(regExp));
        });
    }
}

/**
 * This will be called when a file is dropped.
 */
function onFileDrop(eventObject) {
    // Get the dropped file
    const file = eventObject.dataTransfer.items[0];
    if (!file) {
        throw new Error('file is empty.');
    }

    // Update all the outputs
    const fileObject = file.getAsFile();
    Script.Outputs.File = fileObject;
    Script.Outputs.Path = fileObject.path;
    Script.Outputs.Name = fileObject.name;
    Script.Outputs.SizeInBytes = fileObject.size;
    Script.Outputs.Type = fileObject.type;

    Script.Outputs.Success();
}

/**
 * """
 * A listener for the dragenter and dragover events are used to
 * indicate valid drop targets, that is,
 * places where dragged items may be dropped.
 * 
 * Most areas of a web page or application are not valid places
 * to drop data. Thus, the default handling of these events
 * is not to allow a drop.
 * """
 */

function onDragOver(eventObject) {
    // Check if the user is dragging any files
    if (eventObject.dataTransfer.items.length === 0) {
        // Early exit; the user is not dragging any files.
        return;
    }

    // Check if we have any custom patterns
    if (acceptedFileTypesPatterns.length === 0) {
        // if not then exit early allowing any pattern
        return eventObject.preventDefault();
    }

    const file = eventObject.dataTransfer.items[0];
    if (file.kind !== 'file') {
        // Early exit; we don't know what this file is.
        return;
    }

    for (var i = 0; i < acceptedFileTypesPatterns.length; i++) {
        const pattern = acceptedFileTypesPatterns[i];

        // Check if this file matches any of the patterns
        if (pattern.test(file.type)) {
            // Mark that this file can be dropped
            eventObject.preventDefault();

            Script.Outputs.OnDragOver();

            // Early exit; since we know the file is okay.
            return;
        }
    }
}    

function onDragLeave(_eventObject) {
    console.log('Drag leave triggered');
    Script.Outputs.OnDragLeave();
}

/**
 * 
 */
Node.Signals.DidMount = function () {
    const element = Script.Inputs.element;
    if (!element) throw new Error("Element is not connected.");

    const domElement = element.getDOMElement();
    domElement.addEventListener('drop', onFileDrop, false);
    domElement.addEventListener('dragover', onDragOver, false);
    domElement.addEventListener('dragleave', onDragLeave, false);
}

/**
 * We need to remove the event listeners on the element,
 * to not leave anything behind.
 */
Node.Signals.WillUnmount = function () {
    const element = Script.Inputs.element;
    if (!element) throw new Error("Element is not connected.");

    const domElement = element.getDOMElement();
    if (domElement) {
        domElement.removeEventListener('drop', onFileDrop, false);
        domElement.removeEventListener('dragover', onDragOver, false);
        domElement.removeEventListener('dragleave', onDragLeave, false);
    }
}

