  let ele = Inputs.element.getDOMElement();
  //ele.style.background = Inputs.color1;
  ele.style.background = `linear-gradient(${Inputs.angle}deg, ${formatRGB(Inputs.color1)} 0%, ${formatRGB(Inputs.color2)} 100%)`;
}

function formatRGB(color) {
  let colObj = hexToRGB(color);
  console.log(color, colObj)
  let result = `rgba(${colObj.r},${colObj.g},${colObj.b},${colObj.a})`;
  return result;
}

function hexToRGB(hex) {
  if (hex.length < 8) {
    var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? {
      r: parseInt(result[1], 16),
      g: parseInt(result[2], 16),
      b: parseInt(result[3], 16),
      a: 1
    } : null;
  } else {
    var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? {
      r: parseInt(result[1], 16),
      g: parseInt(result[2], 16),
      b: parseInt(result[3], 16),
      a: parseInt(result[4], 16)
    } : null;
  }
}

