const userId = Noodl.Objects.currentUser.auth.id; 
if (!userId) throw new Error('Invalid User ID');    

try {      
  const { data, error } = await Noodl.Variables.supabase          
    .storage          
    .from('resume_photos')          
    .list(`${userId}/`);        

  if (error) throw error;        

  // Create an array to store the paths      
  let paths = [];        

  // Loop through each photo in the bucket      
  for (let photo of data) {          
    // Check if the photo name is not '.emptyFolderPlaceholder'
    if(photo.name !== '.emptyFolderPlaceholder') {
      // Add the photo name to the paths array with userId and / appended before the image name     
      paths.push(`${userId}/${photo.name}`);      
    }
  }       

  Outputs.Paths = paths;       

  // Create signed URLs for all photos in one request      
  const { data: signedURLs, error: urlsError } = await Noodl.Variables.supabase          
    .storage          
    .from('resume_photos')          
    .createSignedUrls(paths, 600);        

  if (urlsError) throw urlsError;        

  // Merge the data from the first request with the signed URLs from the second request      
  let mergedData = paths.map((path, index) => {          
    // Find the corresponding photo object
    const photo = data.find(photo => `${userId}/${photo.name}` === path);
    // Check if the photo name matches the current image
    return {              
        ...photo,              
        signedUrl: signedURLs[index],
        current: false,
    }; 
  });        

  // Output the merged object with all properties from both requests      
  Outputs.Photos = mergedData;      
  Outputs.Success();  
} catch (error) {      
  Outputs.error = error;      
  Outputs.Failure();  
}

const items = Inputs.Items || [];
const currentImage = Inputs.CurrentImage || 'default';
const limit = Inputs.Limit || 8; // Default limit is 10
const skip = Inputs.Skip || 0; // Default skip is 0

if (!items.length || !currentImage) return;

try {
  // Pagination logic
  const paginatedItems = items.slice(skip, skip + limit);

  paginatedItems.forEach(item => {
    if (item.name === currentImage) {
      item.current = true;
    } else {
      item.current = false;
    }
  });

  Outputs.Items = paginatedItems;
  Outputs.Count = paginatedItems.length; // Output the count of paginated items
  Outputs.Success();
} catch (error) {
  Outputs.error = error;
  Outputs.Failure();
}

