parameters.functionScript:
const data = Inputs.Applicant

if (!data) throw new Error('No data provided')

function obfuscate(text) {
  let obfuscated = '';
  for (let i = 0; i < text.length; i++) {
    obfuscated += '*';
  }
  return obfuscated;
}

Outputs.JobTitle = data.job_title;
Outputs.Profile = data.profile;
Outputs.Hobbies = data.hobbies;

if (Inputs.Anonymise) {
    Outputs.Name = obfuscate(data.name);
    Outputs.Email = obfuscate(data.email);
    Outputs.Phone = obfuscate(data.phone);
} else {
    Outputs.Name = data.name;
    Outputs.Email = data.email;
    Outputs.Phone = data.phone;
}

let path = Inputs.Path;

if (!path) {
    Outputs.Source = '/assets/images/blue-house-horizontal.png';
    return;
}

const supabase = Noodl.Variables.supabase;

const { data, error } = await supabase
  .storage
  .from('resume_photos')
  .createSignedUrl(path, 600)

Outputs.Source = data.signedUrl;
Outputs.Success()

let path = Inputs.Path;
let userPlan = Noodl.Objects.currentUser.plan.plan_id;
let include_watermark = Inputs.IncludeWatermark;

if (include_watermark === false && userPlan != "6aa089bf-0b62-41c2-9b97-467939a2d739") {
  Outputs.WatermarkMounted = false;
} else {
  Outputs.WatermarkMounted = true;
}

if (!path || userPlan === "6aa089bf-0b62-41c2-9b97-467939a2d739") {
    Outputs.Source = '/assets/images/blue-house-horizontal.png';
    return;
}

const supabase = Noodl.Variables.supabase;

const { data, error } = await supabase
  .storage
  .from('resume_photos')
  .createSignedUrl(path, 600)

Outputs.Source = data.signedUrl;
Outputs.Success()

