Outputs.Templates = [
    {
        "id": "template-one",
        "name": "template-one",
        "imgSource": "assets/images/template-one.png"
    },
    {
        "id": "template-two",
        "name": "template-two",
        "imgSource": "assets/images/template-two.png"
    },
    {
        "id": "template-three",
        "name": "template-three",
        "imgSource": "assets/images/template-three.png"
    }
]

const file = Inputs.File || null;
const bucket = 'temp_files';
const supabase = Noodl.Variables.supabase

if (!file) throw new Error('No file provided');

const randomSequence = Math.random().toString(36).substring(2, 8);

try {
  const { data, error } = await supabase.storage
    .from(bucket)
    .upload(`${file.name.split('.').slice(0, -1).join('.')}_${randomSequence}.${file.name.split('.').pop()}`, file);

  if (error) {
    throw new Error(error)
  }  

  const { data: urlData, error: urlError } = await supabase.storage
    .from(bucket)
    .getPublicUrl(data.path)

  if (urlError) {
    throw new Error(urlError)
  }

  Outputs.Data = urlData;
  Outputs.Success();
} catch (error) {
  Outputs.error = error.message;
  Outputs.Failure();
}

